import { Cancel } from '@mui/icons-material';
import {
  IconButton,
  ImageList,
  ImageListItem,
  ImageListItemBar,
} from '@mui/material';
import React from 'react';
import { useValue } from '../../../context/ContextProvider';
import deleteFile  from '../../../firebase/deleteFile'

const ImageLists = () => {
  const {
    state: { images, currentUser },
    dispatch,
  } = useValue();
  console.log(currentUser.result.id)
  console.log(images)
  const handleDelete = async (image) => {
    dispatch({ type: 'DELETE_IMAGE', payload: image });
    
    const imageName = image?.split(`${currentUser?.result.id}%2F`)[1]?.split('?')[0];
    try {
      await deleteFile(`rooms/${currentUser?.result.id}/${imageName}`);
    } catch (error) {
      console.log(error);
    }
  };
  
  return (
    <ImageList
      rowHeight={250}
      sx={{
        '&.MuiImageList-root': {
          gridTemplateColumns:
            'repeat(auto-fill, minmax(250px, 1fr))!important',
        },
      }}
    >
      {images.map((image, index) => (
        <ImageListItem key={index} cols={1} rows={1}>
          <img
            src={image}
            alt="rooms"
            loading="lazy"
            style={{ height: '100%' }}
            
          />
          <ImageListItemBar
            position="top"
            sx={{
              background:
                'linear-gradient(to bottom, rgba(0,0,0,0.7)0%, rgba(0,0,0,0.3)70%, rgba(0,0,0,0)100%)',
            }}
            actionIcon={
              <IconButton
                sx={{ color: 'white' }}
                onClick={() => handleDelete(image)}
              >
                <Cancel />
              </IconButton>
            }
          ></ImageListItemBar>
        </ImageListItem>
      ))}
    </ImageList>
  );
};

export default ImageLists;
